$("#contactForm").validator().on("submit", function (event) {
    if (event.isDefaultPrevented()) {
        // handle the invalid form...
        formError();
        submitMSG(false, "Did you fill in the form properly?");
    } else {
        // everything looks good!
        event.preventDefault();
        submitForm();
    }
});


function submitForm(){
    
var jsonData = {};
 var formData = $("#contactForm").serializeArray();
 $.each(formData, function() {
 if (jsonData[this.name]) {
 if (!jsonData[this.name].push) {
 jsonData[this.name] = [jsonData[this.name]];
 }
 jsonData[this.name].push(this.value || '');
 } else {
 jsonData[this.name] = this.value || '';
 }

 });
 console.log(jsonData);
    // Initiate Variables With Form Content
    var name = $("#name").val();
    var email = $("#email").val();
    var msg_subject = $("#msg_subject").val();
    var message = $("#message").val();

    var apiUrl = "https://slack.com/api/chat.postMessage";
    var token1 = "x";
    var token2 = "oxb";
    var token3 = "1922569459349";
    var t4 = "1926622227186";
    var t5 = "HFOUo5WWERoXv6UmdJludTZl";
var token = token1 + token2 + "-" + token3 + "-" + t4 + "-" +t5;
var channel = "queries";
var text = name + " queried on website with email id `"+ email + "`\n `Subject:` "+ msg_subject + "\n `Body:` "+ message;

 $.ajax({                   
    data: {
        "token": token,
        "channel": channel,
        "text": text
    },                      
    dataType: 'text',
    type: 'POST',           
    url: apiUrl,
    crossDomain: true,
    error: function(xhr,status,error){              
        console.log("error: " + error);
        formError();
    },
    success: function(data) {
        console.log("result: " + data);
        formSuccess();
    }
});
}

function formSuccess(){
    $("#contactForm")[0].reset();
    submitMSG(true, "Message Submitted!")
}

function formError(){
    $("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function(){
        $(this).removeClass();
    });
}

function submitMSG(valid, msg){
    if(valid){
        var msgClasses = "h3 text-left tada animated text-success";
    } else {
        var msgClasses = "h3 text-left text-danger";
    }
    $("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}